var cartAmount=0,addToCartCont=document.getElementById("cart--drawer"),miniCartCont=document.getElementById("mini-cart--drawer"),addToCartText=document.getElementById("addToCart"),productPrice=22.81,cartPrice=document.getElementById("cart__drawer_price"),quantityText=document.getElementById("cart__quantity"),cartCounter=document.getElementById("nav__cart_counter");function addToCart(){addToCartText.innerHTML='<div class="block__cart_loader_cont"><div class="block__cart_loader"></div></div>',openCartDrawer(),updateCartCounter()}function openCartDrawer(){setTimeout(function(){addToCartCont.classList.add("drawer--active"),addToCartText.innerHTML="ADDED",document.getElementById("cart--drawer-confirmation").style.display="block"},1e3)}function updateCartCounter(){cartAmount++,updateQuantity()}function decreaseCartCounter(){1==cartAmount?(cartAmount=0,quantityText.innerHTML="Quantity: "+cartAmount,cartCounter.innerHTML="",setTimeout(function(){closeDrawer()},1e3)):(cartAmount--,updateQuantity())}function openCart(){addToCartCont.classList.add("drawer--active"),document.getElementById("cart--drawer-confirmation").style.display="none"}function updateQuantity(){cartCounter.innerHTML=cartAmount,quantityText.innerHTML="Quantity: "+cartAmount,cartPriceTotal=productPrice*cartAmount,cartPrice.innerHTML="Subtotal: Â£"+cartPriceTotal.toFixed(2)}function closeDrawer(){addToCartCont.classList.remove("drawer--active"),addToCartText.innerHTML="ADD TO CART"}